import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { loginAction } from '../redux/actions/authActions';

const LoginForm = () => {
  const dispatch = useDispatch();
  const isAuthenticated = useSelector((state) => !!state.auth.token);
  const navigate = useNavigate();

  const handleLogin = async (email, password) => {
    const loginSuccessful = await dispatch(loginAction(email, password));
    if (loginSuccessful) {
      navigate('/todos'); // Redirect to TodoList on successful login
    }
  };

  // Render login form UI
  return (
    <>
      {!isAuthenticated ? (
        // Render your login form UI here
        <div>
          <form onSubmit={(e) => handleLogin('your_email', 'your_password')}>
            {/* Your form inputs and submit button */}
            <input type="email" placeholder="Email" />
            <input type="password" placeholder="Password" />
            <button type="submit">Login</button>
          </form>
        </div>
      ) : (
        // If already authenticated, redirect to the todo page
        navigate('/todos')
      )}
    </>
  );
};

export default LoginForm;
