import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { getAllTodos } from '../redux/actions/index';
import Todo from './Todo';
import TodoForm from './TodoForm';
import TodoList from './TodoList';
import Header from './Header';

const Todos = () => {
  const todos = useSelector((state) => state.todos);
  const completedTodos = useSelector((state) => state.completedTodos);
  const deletedTodos = useSelector((state) => state.deletedTodos);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getAllTodos());
  }, [dispatch]);
  if (!Array.isArray(todos) || !Array.isArray(completedTodos) || !Array.isArray(deletedTodos)) {
  // You can display a loading message or an empty state
 console.log("emply");
 return null;
}
  return (
    <div>
      <Header/>
      <TodoForm/>
      <TodoList/>
      <h2>All Todos</h2>
      <ul>
        {todos.map((todo) => (
          <Todo key={todo._id} todo={todo} />
        ))}
      </ul>

      <h2>Completed Todos</h2>
      <ul>
        {completedTodos.map((todo) => (
          <Todo key={todo._id} todo={todo} />
        ))}
      </ul>

      <h2>Deleted Todos</h2>
      <ul>
        {deletedTodos.map((todo) => (
          <Todo key={todo._id} todo={todo} />
        ))}
      </ul>
    </div>
  )}


export default Todos;
