const initialState = {
    message: null,
    error: null,
  };
  
  const authReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'REGISTER_SUCCESS':
        return {
          ...state,
          message: action.payload.message,
          error: null,
        };
      case 'REGISTER_FAIL':
        return {
          ...state,
          message: null,
          error: action.payload,
        };
      default:
        return state;
    }
  };
  
  export default authReducer;
