const Tabs = ({ currentTab }) => {
    return (
      <div>
        <button key="all-todos" className={currentTab === ALL_TODOS ? 'active' : ''}>
          All Todos
        </button>
        <button key="active-todos" className={currentTab === ACTIVE_TODOS ? 'active' : ''}>
          Active Todos
        </button>
        <button key="done-todos" className={currentTab === DONE_TODOS ? 'active' : ''}>
          Done Todos
        </button>
      </div>
    );
  };

export default Tabs;
