const Tabs = ({ currentTab }) => {
    const dispatch = useDispatch();
  
    const TABS = ['All Todos', 'Active Todos', 'Done Todos'];
  
    return (
      TABS.map((tab, index) => (
        <button
          key={index}
          className={tab === currentTab ? 'button selected' : 'button'}
          onClick={() => dispatch(toggleTab(tab))}
        >
          {tab}
        </button>
      ))
    );
  };
  


export default Tabs;
